import React, { useState, useEffect } from 'react';
import './App.css';
import Screen from './components/Screen';
import ScoreDisplay from './components/ScoreDisplay';
import { connectWebSocket, disconnectWebSocket } from './WebSocketService'; // Import WebSocket functions

function App() {
  const [screenColor, setScreenColor] = useState('red');
  const [score, setScore] = useState(0);

  useEffect(() => {
    console.log('WebSocket connected');
    connectWebSocket(handleWebSocketMessage);

    // Clean up the WebSocket connection when the component unmounts
    return () => {
      console.log('WebSocket disconnected');
      disconnectWebSocket();
    };
  }, []);
  // Function to simulate screen color changes
  const changeScreenColor = () => {
    // console.log('change screen color'); screen color is being changes 
    if (screenColor === 'red') {
      setScreenColor('green');
    } else {
      setScreenColor('red');
    }
  };

  // Simulate screen color changes at a random interval
  useEffect(() => {
    // console.log('interval changed'); iterval is being changed 
    const intervalId = setInterval(changeScreenColor, Math.random() * 2000 + 1000);
    // Clean up the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, [screenColor]);



  // Callback function to handle WebSocket messages
  const handleWebSocketMessage = (message) => {
    console.log('Received message:', message);

    // Assuming that the incoming message is an array of screen colors
    if (Array.isArray(message)) {
        // Assuming the last item in the array represents the active screen color
        const activeScreenColor = message[message.length - 1];

        // Update the screen color based on the received active screen color
        setScreenColor(activeScreenColor);
    }
};


  return (
    <div className="App">
      <h1>Phone App</h1>
      <Screen color={screenColor} />
      <ScoreDisplay score={score} />
    </div>
  );
}

export default App;
