// Screen.js

import React from 'react';

const Screen = ({ color }) => {
  const screenStyle = {
    backgroundColor: color,
    width: '100px', // Adjust the size as needed
    height: '100px',
    borderRadius: '50%', // Make it round
  };

  return <div className="screen" style={screenStyle}></div>;
};

export default Screen;
